function addId(next) {
  const recursion = (passedIn) => {
    const it = (passedIn) || this;
    if (it.type === 'dir') {
      it.content.forEach((item) => {
        item._id = mongoose.Types.ObjectId();
        recursion(item);
      });
    } else {
      it._id = mongoose.Types.ObjectId();
    }
  };
  recursion();
  next();
}
