  display: flex;
  flex-direction: column;
  justify-items: center;
  align-items: center;

}
.main-title {
  grid-row: 1/2;
  grid-column: 2/3;
  justify-self: center;
  text-transform: uppercase;
  letter-spacing: 0.8px;
  margin: 20px 40px;
}
.title-2 {
  font-size: 1.9em;
  font-weight: 100;
}
.card-container {
  grid-column: 2/3;
  display: flex;
  align-items: center;
  align-self: center;
  padding: 8px;
  margin: 20px;
  border-radius: 5px;
  -webkit-box-shadow: -1px 2px 5px -1px rgba(0, 0, 0, 0.62);
  -moz-box-shadow: -1px 2px 5px -1px rgba(0, 0, 0, 0.62);
  box-shadow: -1px 2px 5px -1px rgba(0, 0, 0, 0.62);
}
/* UNDERLINE THE ELEMENT BLUE WHEN HOVERED */
.hover-underline-blue::after {
  background: none repeat scroll 0 0 transparent;
  bottom: -2px;
  content: "";
  display: block;
  height: 2px;
  left: 15%;
  position: relative;
  background: var(--primary-color);
  transition: width 0.4s ease 0s, left 0.4s ease 0s;
  width: 0%;
}
.hover-underline-blue:hover::after {
  width: 100%;
  left: 0;
}
/* UNDERLINE THE ELEMENT YELLOW WHEN HOVERED */
.hover-underline-yellow::after {
  background: none repeat scroll 0 0 transparent;
  bottom: -2px;
  content: "";
  display: block;
  height: 2px;
  left: 15%;
  position: relative;
  background: var(--secondary-color);
  transition: width 0.4s ease 0s, left 0.4s ease 0s;
  width: 0%;
}
.hover-underline-yellow:hover::after {
  width: 100%;
  left: 0;
}

.footer {
  grid-area: "footer";
}
.submit-btn {
  grid-row: 2/3;
  height: 40px;
  border-width: 0;
  border-radius: 5px;
  white-space: nowrap;
  box-shadow: 1px 1px 0px 1px rgba(0, 0, 0, 0.05);
  transition-property: background-color, box-shadow;
  transition-duration: 150ms;
  transition-timing-function: ease-in-out;
  padding: 0;
  margin: 10px;
  cursor: pointer;
}
.submit-btn:hover {
  box-shadow: 1px 4px 5px 1px rgba(0, 0, 0, 0.301);
}
.submit-btn:active {
  position: relative;
  top: 1px;
}
@media screen and (min-width: 780px) {
  .app {
    display: grid;
    grid-template-rows: .5fr 2fr .5fr;
    grid-template-areas:
      "header"
      "main"
      "footer";
    width: 100%;
    height: 100vh;
  }

  .header {
    grid-area: "header";
    padding: 10px 0px 20px 0px;
  }


  .header title {
    grid-area: "up";

  }

  .main {
    grid-area: "main";

  }

  .footer {
    grid-area: "footer";
  }
}

.footer div {
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-content: center;
  margin: 15px;
}

.social {
  margin: 15px;
}
.footer-credits {
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
}

@media screen and (min-width: 780px) {
  .footer-credits {
    display: flex;
    flex-direction: column;
  }
}

@import url("https://fonts.googleapis.com/css?family=Raleway&display=swap");
@import url("https://fonts.googleapis.com/css?family=Lato&display=swap");
@import "https://cdnjs.cloudflare.com/ajax/libs/animate.css/4.0.0/animate.min.css";
@import url("https://fonts.googleapis.com/css2?family=Bebas+Neue&display=swap");

:root {
  --primary-color: rgb(35, 35, 61);
  --secondary-color: rgb(251, 211, 3);
  --light: #fbf7f4;
  --text-shadow: 0px 1px 2px var(--primary-color);

}

body {
  padding: 0px;
  margin: 0px;
  font-family: "Raleway", serif;
}

.primary {
  background-color: var(--primary-color);
  color: white;
}
.secondary {
  background-color: var(--secondary-color);
}
.secondary--text {
  color: var(--secondary-color);
}
.primary--text {
  color: var(--primary-color);
}

.light {
  background-color: var(--light);
}

.fab-btn {
  border-radius: 30px;
}

.neom {
  box-shadow: 12px 12px 24px 0 rgba(0, 0, 0, 0.2),
    -12px -12px 24px 0 rgba(243, 243, 243, 0.637);
}
.shadow {
  -webkit-box-shadow: -1px 2px 8px -1px rgba(0, 0, 0, 0.62);
  -moz-box-shadow: -1px 2px 8px -1px rgba(0, 0, 0, 0.62);
  box-shadow: -1px 2px 8px -1px rgba(0, 0, 0, 0.62);
}

.title-font {
  font-family: "Bebas Neue", sans-serif;
}

.bold {
  font-weight: 700;
}

.hoverable-card {
  transition: cubic-bezier(0.47, 0, 0.745, 0.715);
  transition-delay: 0.1s;
  transition-duration: 0.3s;
}

.hoverable-card:active,
.hoverable-card:focus,
.hoverable-card:hover {
  transform: translate3d(2px, -15px, -90px);
  filter: brightness(50%);
  -webkit-box-shadow: -1px 2px 8px -1px rgba(0, 0, 0, 0.62);
  -moz-box-shadow: -1px 2px 8px -1px rgba(0, 0, 0, 0.62);
  box-shadow: -1px 2px 8px -1px rgba(0, 0, 0, 0.62);
}

.mw-robot {
  width: 70px;
  padding-bottom: 2px;
}
.double-w {
  margin: 5px;
  font-family: "Lato", serif;
  font-size: 2.3em;
  animation: glow 4s infinite alternate;
  font-weight: 500;
}

.programming {
  padding: 0;
  margin: 5px;
  font-size: 1.2em;
  letter-spacing: 3.2px;
  font-weight: 400;
}
.title div {
  display: flex;
  flex-direction: column;
  justify-items: center;
  align-items: center;
}
.title {
  display: flex;
  flex-wrap: nowrap;
  flex-direction: column;
  justify-items: center;
  align-items: center;
  padding-top: 15px;
}

@media screen and (min-width: 780px) {
  .title {
    display: grid;
    grid-template-columns: 1.6fr 1.3fr 2fr;
  }
  .mw-robot {
    grid-column: 1/2;
    width: 132px;
    height: 125px;
    justify-content: end;
    margin: 20px 0px;
  }
  .double-w {
    grid-column: 2/3;
    font-family: "Lato", serif;
    font-size: 2.9em;
    justify-self: end;
    align-self: center;
    font-weight: 500;
  }

  .programming {
    grid-column: 3/-1;
    font-size: 2em;
    justify-self: start;
    padding-left: 20px;
    letter-spacing: 3.5px;
    font-weight: 300;
  }
}
@keyframes glow {
  to {
    text-shadow: 0 0 8px var(--secondary-color);
  }
}
